import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import {
    LayoutDashboard,
    Folder,
    FolderOpen,
    Package,
    ShoppingCart,
    LogOut,
    Menu,
    X,
    Clock,
    CheckCircle,
    Key,
    TrendingUp,
    DollarSign
} from 'lucide-react';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Area, AreaChart } from 'recharts';
import Categories from './Categories';
import SubCategories from './SubCategories';
import Products from './Products';
import Orders from './Orders';
import ChangePassword from './ChangePassword';
import './Dashboard.css';

function Dashboard() {
    const navigate = useNavigate();
    const location = useLocation();
    const [adminUser, setAdminUser] = useState(null);
    const [sidebarOpen, setSidebarOpen] = useState(false);

    useEffect(() => {
        const user = localStorage.getItem('adminUser');
        if (user) {
            setAdminUser(JSON.parse(user));
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('adminToken');
        localStorage.removeItem('adminUser');
        navigate('/');
    };

    const isActive = (path) => location.pathname === path;
    const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
                <div className="sidebar-header">
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div>
                            <h2 className="brand-title">LESTORA</h2>

                        </div>
                        <button
                            className="lg:hidden text-white"
                            onClick={toggleSidebar}
                            style={{ background: 'none', border: 'none', cursor: 'pointer', display: window.innerWidth <= 768 ? 'block' : 'none' }}
                        >
                            <X size={24} color="white" />
                        </button>
                    </div>
                </div>

                <nav className="sidebar-nav">
                    <NavLink to="/dashboard" active={isActive('/dashboard')} icon={<LayoutDashboard size={20} />}>
                        Dashboard
                    </NavLink>
                    <NavLink to="/dashboard/categories" active={isActive('/dashboard/categories')} icon={<Folder size={20} />}>
                        Categories
                    </NavLink>
                    <NavLink to="/dashboard/subcategories" active={isActive('/dashboard/subcategories')} icon={<FolderOpen size={20} />}>
                        SubCategories
                    </NavLink>
                    <NavLink to="/dashboard/products" active={isActive('/dashboard/products')} icon={<Package size={20} />}>
                        Products
                    </NavLink>
                    <NavLink to="/dashboard/orders" active={isActive('/dashboard/orders')} icon={<ShoppingCart size={20} />}>
                        Orders
                    </NavLink>
                    <NavLink to="/dashboard/change-password" active={isActive('/dashboard/change-password')} icon={<Key size={20} />}>
                        Change Password
                    </NavLink>
                </nav>

                <div className="sidebar-footer">
                    <button onClick={handleLogout} className="logout-btn">
                        <LogOut size={18} style={{ marginRight: '8px' }} />
                        Logout
                    </button>
                </div>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                <header className="top-header">
                    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                        <button
                            onClick={toggleSidebar}
                            style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                display: window.innerWidth <= 768 ? 'block' : 'none'
                            }}
                        >
                            <Menu size={24} color="#002b36" />
                        </button>
                        <h1 className="header-title">
                            {getPageTitle(location.pathname)}
                        </h1>
                    </div>
                </header>

                <div className="content-wrapper">
                    <Routes>
                        <Route index element={<DashboardHome />} />
                        <Route path="categories" element={<Categories />} />
                        <Route path="subcategories" element={<SubCategories />} />
                        <Route path="products" element={<Products />} />
                        <Route path="orders" element={<Orders />} />
                        <Route path="change-password" element={<ChangePassword />} />
                    </Routes>
                </div>
            </main>
        </div>
    );
}

function NavLink({ to, active, icon, children }) {
    return (
        <Link to={to} className={`nav-link ${active ? 'active' : ''}`}>
            <span className="nav-icon">{icon}</span>
            {children}
        </Link>
    );
}

function DashboardHome() {
    const [stats, setStats] = useState({
        totalCategories: 0,
        totalProducts: 0,
        totalOrders: 0,
        pendingOrders: 0
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDashboardStats();
    }, []);

    const fetchDashboardStats = async () => {
        try {
            const { categoryAPI, productAPI, orderAPI } = await import('../services/api');
            const [categoriesRes, productsRes, ordersRes] = await Promise.all([
                categoryAPI.getAll(),
                productAPI.getAll(),
                orderAPI.getAll()
            ]);

            const totalCategories = categoriesRes.data.categories?.length || 0;
            const totalProducts = productsRes.data.products?.length || 0;
            const totalOrders = ordersRes.data.orders?.length || 0;
            const pendingOrders = ordersRes.data.orders?.filter(order => order.status === 'pending').length || 0;

            setStats({
                totalCategories,
                totalProducts,
                totalOrders,
                pendingOrders
            });
        } catch (error) {
            console.error('Failed to fetch dashboard stats:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', padding: '3rem' }}>
                <div className="spinner"></div>
            </div>
        );
    }

    // Prepare data for charts
    const monthlyOrderData = [
        { month: 'Jan', orders: 45, pending: 12 },
        { month: 'Feb', orders: 52, pending: 15 },
        { month: 'Mar', orders: 38, pending: 8 },
        { month: 'Apr', orders: 65, pending: 18 },
        { month: 'May', orders: 58, pending: 14 },
        { month: 'Jun', orders: stats.totalOrders || 48, pending: stats.pendingOrders || 10 }
    ];

    const trafficData = [
        { name: 'Categories', value: stats.totalCategories },
        { name: 'Products', value: stats.totalProducts }
    ];

    const salesData = [
        { month: 'Jan', sales: 4200 },
        { month: 'Feb', sales: 5100 },
        { month: 'Mar', sales: 3800 },
        { month: 'Apr', sales: 6500 },
        { month: 'May', sales: 5800 },
        { month: 'Jun', sales: 6820 }
    ];

    const DONUT_COLORS = ['#3b82f6', '#f97316'];

    const totalTraffic = trafficData.reduce((sum, item) => sum + item.value, 0);
    const trafficPercentage = totalTraffic > 0 ? ((trafficData[0].value / totalTraffic) * 100).toFixed(1) : 0;

    return (
        <div className="dashboard-modern">
            {/* Main Grid Layout */}
            <div className="dashboard-grid">
                {/* Left Column - Large Charts */}
                <div className="dashboard-left">
                    {/* Orders Bar Chart */}
                    <div className="chart-card-modern">
                        <div className="chart-header">
                            <h3>Orders Overview</h3>
                            <span className="chart-subtitle">Monthly statistics</span>
                        </div>
                        <ResponsiveContainer width="100%" height={280}>
                            <BarChart data={monthlyOrderData}>
                                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" vertical={false} />
                                <XAxis dataKey="month" stroke="#94a3b8" fontSize={12} />
                                <YAxis stroke="#94a3b8" fontSize={12} />
                                <Tooltip
                                    contentStyle={{
                                        backgroundColor: 'white',
                                        border: '1px solid #e2e8f0',
                                        borderRadius: '8px',
                                        boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
                                    }}
                                />
                                <Bar dataKey="orders" fill="#3b82f6" radius={[8, 8, 0, 0]} />
                                <Bar dataKey="pending" fill="#f97316" radius={[8, 8, 0, 0]} />
                            </BarChart>
                        </ResponsiveContainer>
                    </div>

                    {/* Recent Transactions */}
                    <div className="chart-card-modern">
                        <div className="chart-header">
                            <h3>Recent Activity</h3>
                            <span className="chart-subtitle">Latest updates</span>
                        </div>
                        <div className="transactions-list">
                            <div className="transaction-item">
                                <div className="transaction-icon" style={{ backgroundColor: '#dbeafe' }}>
                                    <ShoppingCart size={18} color="#3b82f6" />
                                </div>
                                <div className="transaction-details">
                                    <p className="transaction-title">New order received</p>
                                    <span className="transaction-time">Order #{stats.totalOrders || '001'}</span>
                                </div>
                                <span className="transaction-amount">+${((stats.totalOrders || 1) * 45).toFixed(2)}</span>
                            </div>
                            <div className="transaction-item">
                                <div className="transaction-icon" style={{ backgroundColor: '#fed7aa' }}>
                                    <Package size={18} color="#f97316" />
                                </div>
                                <div className="transaction-details">
                                    <p className="transaction-title">Product added</p>
                                    <span className="transaction-time">SKU #{stats.totalProducts || '001'}</span>
                                </div>
                                <span className="transaction-amount">New</span>
                            </div>
                            <div className="transaction-item">
                                <div className="transaction-icon" style={{ backgroundColor: '#d1fae5' }}>
                                    <CheckCircle size={18} color="#10b981" />
                                </div>
                                <div className="transaction-details">
                                    <p className="transaction-title">Order completed</p>
                                    <span className="transaction-time">Order #{(stats.totalOrders || 1) - (stats.pendingOrders || 0)}</span>
                                </div>
                                <span className="transaction-amount success">Completed</span>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Right Column - Widgets */}
                <div className="dashboard-right">
                    {/* Traffic Distribution Donut */}
                    <div className="chart-card-modern compact">
                        <div className="chart-header">
                            <h3>Traffic Distribution</h3>
                        </div>
                        <div className="donut-chart-container">
                            <div className="donut-center-value">
                                <h2>${totalTraffic.toLocaleString()}</h2>
                                <p className="donut-percentage">
                                    <TrendingUp size={14} color="#10b981" />
                                    +{trafficPercentage}% last year
                                </p>
                                <div className="donut-legend">
                                    <div className="legend-item">
                                        <span className="legend-dot" style={{ backgroundColor: '#3b82f6' }}></span>
                                        <span>Categories</span>
                                    </div>
                                    <div className="legend-item">
                                        <span className="legend-dot" style={{ backgroundColor: '#f97316' }}></span>
                                        <span>Products</span>
                                    </div>
                                </div>
                            </div>
                            <ResponsiveContainer width="100%" height={200}>
                                <PieChart>
                                    <Pie
                                        data={trafficData}
                                        cx="50%"
                                        cy="50%"
                                        innerRadius={60}
                                        outerRadius={80}
                                        paddingAngle={5}
                                        dataKey="value"
                                    >
                                        {trafficData.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={DONUT_COLORS[index]} />
                                        ))}
                                    </Pie>
                                </PieChart>
                            </ResponsiveContainer>
                        </div>
                    </div>

                    {/* Product Sales Line Chart */}
                    <div className="chart-card-modern compact">
                        <div className="chart-header">
                            <h3>Product Sales</h3>
                            <div className="sales-badge">
                                <DollarSign size={16} />
                                <span>$6,820</span>
                            </div>
                        </div>
                        <p className="sales-growth">
                            <TrendingUp size={14} color="#10b981" />
                            +9% last year
                        </p>
                        <ResponsiveContainer width="100%" height={180}>
                            <AreaChart data={salesData}>
                                <defs>
                                    <linearGradient id="salesGradient" x1="0" y1="0" x2="0" y2="1">
                                        <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3} />
                                        <stop offset="95%" stopColor="#3b82f6" stopOpacity={0} />
                                    </linearGradient>
                                </defs>
                                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" vertical={false} />
                                <XAxis dataKey="month" stroke="#94a3b8" fontSize={11} hide />
                                <YAxis stroke="#94a3b8" fontSize={11} hide />
                                <Tooltip
                                    contentStyle={{
                                        backgroundColor: 'white',
                                        border: '1px solid #e2e8f0',
                                        borderRadius: '8px',
                                        boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
                                    }}
                                />
                                <Area
                                    type="monotone"
                                    dataKey="sales"
                                    stroke="#3b82f6"
                                    strokeWidth={2}
                                    fill="url(#salesGradient)"
                                />
                            </AreaChart>
                        </ResponsiveContainer>
                    </div>
                </div>
            </div>
        </div>
    );
}

function StatCard({ title, value, icon, color }) {
    return (
        <div className="stat-card">
            <div className="stat-icon-wrapper" style={{ backgroundColor: `${color}20`, color: color }}>
                {icon}
            </div>
            <div className="stat-info">
                <h3>{title}</h3>
                <p>{value}</p>
            </div>
        </div>
    );
}

function getPageTitle(pathname) {
    const titles = {
        '/dashboard': 'Dashboard Overview',
        '/dashboard/categories': 'Category Management',
        '/dashboard/subcategories': 'SubCategory Management',
        '/dashboard/products': 'Product Inventory',
        '/dashboard/orders': 'Order Management',
        '/dashboard/change-password': 'Change Password'
    };
    return titles[pathname] || 'Dashboard';
}

export default Dashboard;
