import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import {
    LayoutDashboard,
    Folder,
    FolderOpen,
    Package,
    ShoppingCart,
    LogOut,
    Menu,
    X,
    Clock,
    CheckCircle
} from 'lucide-react';
import Categories from './Categories';
import SubCategories from './SubCategories';
import Products from './Products';
import Orders from './Orders';
import './Dashboard.css';

function Dashboard() {
    const navigate = useNavigate();
    const location = useLocation();
    const [adminUser, setAdminUser] = useState(null);
    const [sidebarOpen, setSidebarOpen] = useState(false);

    useEffect(() => {
        const user = localStorage.getItem('adminUser');
        if (user) {
            setAdminUser(JSON.parse(user));
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('adminToken');
        localStorage.removeItem('adminUser');
        navigate('/');
    };

    const isActive = (path) => location.pathname === path;
    const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
                <div className="sidebar-header">
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>

                        <button
                            className="lg:hidden text-white"
                            onClick={toggleSidebar}
                            style={{ background: 'none', border: 'none', cursor: 'pointer', display: window.innerWidth <= 768 ? 'block' : 'none' }}
                        >
                            <X size={24} color="white" />
                        </button>
                    </div>
                </div>

                <nav className="sidebar-nav">
                    <NavLink to="/dashboard" active={isActive('/dashboard')} icon={<LayoutDashboard size={20} />}>
                        Dashboard
                    </NavLink>
                    <NavLink to="/dashboard/categories" active={isActive('/dashboard/categories')} icon={<Folder size={20} />}>
                        Categories
                    </NavLink>
                    <NavLink to="/dashboard/subcategories" active={isActive('/dashboard/subcategories')} icon={<FolderOpen size={20} />}>
                        SubCategories
                    </NavLink>
                    <NavLink to="/dashboard/products" active={isActive('/dashboard/products')} icon={<Package size={20} />}>
                        Products
                    </NavLink>
                    <NavLink to="/dashboard/orders" active={isActive('/dashboard/orders')} icon={<ShoppingCart size={20} />}>
                        Orders
                    </NavLink>
                </nav>

                <div className="sidebar-footer">
                    <button onClick={handleLogout} className="logout-btn">
                        <LogOut size={18} style={{ marginRight: '8px' }} />
                        Logout
                    </button>
                </div>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                <header className="top-header">
                    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                        <button
                            onClick={toggleSidebar}
                            style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                display: window.innerWidth <= 768 ? 'block' : 'none'
                            }}
                        >
                            <Menu size={24} color="#002b36" />
                        </button>
                        <h1 className="header-title">
                            {getPageTitle(location.pathname)}
                        </h1>
                    </div>
                </header>

                <div className="content-wrapper">
                    <Routes>
                        <Route index element={<DashboardHome />} />
                        <Route path="categories" element={<Categories />} />
                        <Route path="subcategories" element={<SubCategories />} />
                        <Route path="products" element={<Products />} />
                        <Route path="orders" element={<Orders />} />
                    </Routes>
                </div>
            </main>
        </div>
    );
}

function NavLink({ to, active, icon, children }) {
    return (
        <Link to={to} className={`nav-link ${active ? 'active' : ''}`}>
            <span className="nav-icon">{icon}</span>
            {children}
        </Link>
    );
}

function DashboardHome() {
    const [stats, setStats] = useState({
        totalCategories: 0,
        totalProducts: 0,
        totalOrders: 0,
        pendingOrders: 0
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDashboardStats();
    }, []);

    const fetchDashboardStats = async () => {
        try {
            const { categoryAPI, productAPI, orderAPI } = await import('../services/api');
            const [categoriesRes, productsRes, ordersRes] = await Promise.all([
                categoryAPI.getAll(),
                productAPI.getAll(),
                orderAPI.getAll()
            ]);

            const totalCategories = categoriesRes.data.categories?.length || 0;
            const totalProducts = productsRes.data.products?.length || 0;
            const totalOrders = ordersRes.data.orders?.length || 0;
            const pendingOrders = ordersRes.data.orders?.filter(order => order.status === 'pending').length || 0;

            setStats({
                totalCategories,
                totalProducts,
                totalOrders,
                pendingOrders
            });
        } catch (error) {
            console.error('Failed to fetch dashboard stats:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', padding: '3rem' }}>
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div>
            <div className="stats-grid">
                <StatCard title="Total Categories" value={stats.totalCategories} icon={<Folder size={24} />} color="#667eea" />
                <StatCard title="Total Products" value={stats.totalProducts} icon={<Package size={24} />} color="#f093fb" />
                <StatCard title="Total Orders" value={stats.totalOrders} icon={<ShoppingCart size={24} />} color="#10b981" />
                <StatCard title="Pending Orders" value={stats.pendingOrders} icon={<Clock size={24} />} color="#f59e0b" />
            </div>

            <div className="stat-card" style={{ flexDirection: 'column', alignItems: 'flex-start' }}>
                <h2 style={{ fontSize: '1.5rem', fontWeight: '600', marginBottom: '1rem', color: '#002b36' }}>
                    Welcome to LESTORA. Admin
                </h2>
                <p style={{ color: '#64748b', lineHeight: '1.6', marginBottom: '1.5rem' }}>
                    Manage your e-commerce store with ease. Use the sidebar navigation to access your inventory, orders, and categories.
                </p>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem', width: '100%' }}>
                    <div style={{ padding: '1rem', background: '#f8fafc', borderRadius: '8px', border: '1px solid #e2e8f0' }}>
                        <strong style={{ color: '#002b36', display: 'block', marginBottom: '0.5rem' }}>Products</strong>
                        <span style={{ fontSize: '0.9rem', color: '#64748b' }}>Add, edit, and manage inventory</span>
                    </div>
                    <div style={{ padding: '1rem', background: '#f8fafc', borderRadius: '8px', border: '1px solid #e2e8f0' }}>
                        <strong style={{ color: '#002b36', display: 'block', marginBottom: '0.5rem' }}>Orders</strong>
                        <span style={{ fontSize: '0.9rem', color: '#64748b' }}>Track and process customer orders</span>
                    </div>
                    <div style={{ padding: '1rem', background: '#f8fafc', borderRadius: '8px', border: '1px solid #e2e8f0' }}>
                        <strong style={{ color: '#002b36', display: 'block', marginBottom: '0.5rem' }}>Categories</strong>
                        <span style={{ fontSize: '0.9rem', color: '#64748b' }}>Organize your store catalog</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

function StatCard({ title, value, icon, color }) {
    return (
        <div className="stat-card">
            <div className="stat-icon-wrapper" style={{ backgroundColor: `${color}20`, color: color }}>
                {icon}
            </div>
            <div className="stat-info">
                <h3>{title}</h3>
                <p>{value}</p>
            </div>
        </div>
    );
}

function getPageTitle(pathname) {
    const titles = {
        '/dashboard': 'Dashboard Overview',
        '/dashboard/categories': 'Category Management',
        '/dashboard/subcategories': 'SubCategory Management',
        '/dashboard/products': 'Product Inventory',
        '/dashboard/orders': 'Order Management'
    };
    return titles[pathname] || 'Dashboard';
}

export default Dashboard;
