import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import Categories from './Categories';
import SubCategories from './SubCategories';
import Products from './Products';
import Orders from './Orders';

function Dashboard() {
    const navigate = useNavigate();
    const location = useLocation();
    const [adminUser, setAdminUser] = useState(null);

    useEffect(() => {
        const user = localStorage.getItem('adminUser');
        if (user) {
            setAdminUser(JSON.parse(user));
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('adminToken');
        localStorage.removeItem('adminUser');
        navigate('/');
    };

    const isActive = (path) => location.pathname === path;

    return (
        <div style={{ display: 'flex', minHeight: '100vh' }}>
            {/* Sidebar */}
            <aside style={{
                width: '280px',
                background: 'linear-gradient(180deg, #1f2937 0%, #111827 100%)',
                color: 'white',
                padding: '2rem 0',
                position: 'fixed',
                height: '100vh',
                overflowY: 'auto'
            }}>
                <div style={{ padding: '0 1.5rem', marginBottom: '2rem' }}>
                    <h2 style={{
                        fontSize: 'var(--font-size-2xl)',
                        fontWeight: '800',
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                    }}>
                        Admin Panel
                    </h2>
                    {adminUser && (
                        <p style={{ fontSize: 'var(--font-size-sm)', color: '#9ca3af', marginTop: '0.5rem' }}>
                            Welcome, {adminUser.username}
                        </p>
                    )}
                </div>

                <nav>
                    <NavLink to="/dashboard" active={isActive('/dashboard')} icon="ðŸ“Š">
                        Dashboard
                    </NavLink>
                    <NavLink to="/dashboard/categories" active={isActive('/dashboard/categories')} icon="ðŸ“">
                        Categories
                    </NavLink>
                    <NavLink to="/dashboard/subcategories" active={isActive('/dashboard/subcategories')} icon="ðŸ“‚">
                        SubCategories
                    </NavLink>
                    <NavLink to="/dashboard/products" active={isActive('/dashboard/products')} icon="ðŸ“¦">
                        Products
                    </NavLink>
                    <NavLink to="/dashboard/orders" active={isActive('/dashboard/orders')} icon="ðŸ›’">
                        Orders
                    </NavLink>
                </nav>

                <div style={{ padding: '0 1.5rem', marginTop: 'auto', paddingTop: '2rem' }}>
                    <button
                        onClick={handleLogout}
                        className="btn btn-danger"
                        style={{ width: '100%' }}
                    >
                        ðŸšª Logout
                    </button>
                </div>
            </aside>

            {/* Main Content */}
            <main style={{
                marginLeft: '280px',
                flex: 1,
                background: 'var(--bg-secondary)',
                minHeight: '100vh'
            }}>
                <header style={{
                    background: 'var(--bg-primary)',
                    padding: '1.5rem 2rem',
                    boxShadow: 'var(--shadow-sm)',
                    position: 'sticky',
                    top: 0,
                    zIndex: 10
                }}>
                    <h1 style={{ fontSize: 'var(--font-size-2xl)', fontWeight: '700' }}>
                        {getPageTitle(location.pathname)}
                    </h1>
                </header>

                <div style={{ padding: '2rem' }}>
                    <Routes>
                        <Route index element={<DashboardHome />} />
                        <Route path="categories" element={<Categories />} />
                        <Route path="subcategories" element={<SubCategories />} />
                        <Route path="products" element={<Products />} />
                        <Route path="orders" element={<Orders />} />
                    </Routes>
                </div>
            </main>
        </div>
    );
}

function NavLink({ to, active, icon, children }) {
    return (
        <Link
            to={to}
            style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.75rem',
                padding: '0.875rem 1.5rem',
                color: active ? 'white' : '#9ca3af',
                textDecoration: 'none',
                fontSize: 'var(--font-size-sm)',
                fontWeight: active ? '600' : '500',
                background: active ? 'rgba(102, 126, 234, 0.2)' : 'transparent',
                borderLeft: active ? '4px solid #667eea' : '4px solid transparent',
                transition: 'all var(--transition-base)'
            }}
        >
            <span style={{ fontSize: '1.25rem' }}>{icon}</span>
            {children}
        </Link>
    );
}

function DashboardHome() {
    const [stats, setStats] = useState({
        totalCategories: 0,
        totalProducts: 0,
        totalOrders: 0,
        pendingOrders: 0
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDashboardStats();
    }, []);

    const fetchDashboardStats = async () => {
        try {
            // Import the API functions
            const { categoryAPI, productAPI, orderAPI } = await import('../services/api');

            // Fetch all data in parallel
            const [categoriesRes, productsRes, ordersRes] = await Promise.all([
                categoryAPI.getAll(),
                productAPI.getAll(),
                orderAPI.getAll()
            ]);

            // Calculate stats
            const totalCategories = categoriesRes.data.categories?.length || 0;
            const totalProducts = productsRes.data.products?.length || 0;
            const totalOrders = ordersRes.data.orders?.length || 0;
            const pendingOrders = ordersRes.data.orders?.filter(order => order.status === 'pending').length || 0;

            setStats({
                totalCategories,
                totalProducts,
                totalOrders,
                pendingOrders
            });
        } catch (error) {
            console.error('Failed to fetch dashboard stats:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div style={{ display: 'flex', justifyContent: 'center', padding: '3rem' }}>
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
                <StatCard title="Total Categories" value={stats.totalCategories} icon="ðŸ“" color="#667eea" />
                <StatCard title="Total Products" value={stats.totalProducts} icon="ðŸ“¦" color="#f093fb" />
                <StatCard title="Total Orders" value={stats.totalOrders} icon="ðŸ›’" color="#10b981" />
                <StatCard title="Pending Orders" value={stats.pendingOrders} icon="â³" color="#f59e0b" />
            </div>

            <div className="card">
                <h2 style={{ fontSize: 'var(--font-size-xl)', fontWeight: '700', marginBottom: '1rem' }}>
                    Welcome to Admin Panel
                </h2>
                <p style={{ color: 'var(--text-secondary)', lineHeight: '1.8' }}>
                    Manage your e-commerce store from this dashboard. Use the sidebar navigation to access different sections:
                </p>
                <ul style={{ marginTop: '1rem', color: 'var(--text-secondary)', lineHeight: '2' }}>
                    <li><strong>Categories:</strong> Create and manage product categories</li>
                    <li><strong>SubCategories:</strong> Organize products with subcategories</li>
                    <li><strong>Products:</strong> Add, edit, and manage your product inventory</li>
                    <li><strong>Orders:</strong> View and manage customer orders</li>
                </ul>
            </div>
        </div>
    );
}

function StatCard({ title, value, icon, color }) {
    return (
        <div className="card" style={{
            background: `linear-gradient(135deg, ${color}15 0%, ${color}05 100%)`,
            borderLeft: `4px solid ${color}`
        }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div>
                    <p style={{ fontSize: 'var(--font-size-sm)', color: 'var(--text-secondary)', marginBottom: '0.5rem' }}>
                        {title}
                    </p>
                    <p style={{ fontSize: 'var(--font-size-3xl)', fontWeight: '800', color: 'var(--text-primary)' }}>
                        {value}
                    </p>
                </div>
                <div style={{ fontSize: '3rem', opacity: 0.3 }}>
                    {icon}
                </div>
            </div>
        </div>
    );
}

function getPageTitle(pathname) {
    const titles = {
        '/dashboard': 'Dashboard',
        '/dashboard/categories': 'Categories',
        '/dashboard/subcategories': 'SubCategories',
        '/dashboard/products': 'Products',
        '/dashboard/orders': 'Orders'
    };
    return titles[pathname] || 'Dashboard';
}

export default Dashboard;
