import React, { useState } from 'react';
import { Moon, Sun } from 'lucide-react';

function Login() {
    const [credentials, setCredentials] = useState({ username: '', password: '' });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [isDark, setIsDark] = useState(true);

    const handleSubmit = (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        // Simulated API call
        setTimeout(() => {
            if (credentials.username === 'admin' && credentials.password === 'admin') {
                alert('Login successful!');
            } else {
                setError('Invalid credentials. Please try again.');
            }
            setLoading(false);
        }, 1000);
    };

    return (
        <div className="min-h-screen flex" style={{
            backgroundColor: isDark ? '#0a2e2e' : '#f5f5f5',
            transition: 'background-color 0.3s'
        }}>
            {/* Left Side - Branding */}
            <div className="hidden lg:flex lg:w-1/2 items-center justify-center relative">
                <div className="absolute top-0 bottom-0 right-0 w-px" style={{
                    backgroundColor: isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)'
                }}></div>

                <div className="text-center px-12">
                    {/* Logo */}
                    <div className="flex items-center justify-center gap-3 mb-4">
                        <svg width="60" height="80" viewBox="0 0 60 80" fill="none">
                            <path d="M20 10 Q30 0 40 10 L40 40 Q30 50 20 40 Z" fill="#22d3ee" />
                            <path d="M20 40 Q30 30 40 40 L40 70 Q30 80 20 70 Z" fill="#f97316" />
                        </svg>
                    </div>

                    <h1 className="text-4xl font-light tracking-wider" style={{
                        color: isDark ? '#ffffff' : '#0a2e2e',
                        fontFamily: 'system-ui, -apple-system, sans-serif'
                    }}>
                        Bailey and Co.
                    </h1>
                </div>
            </div>

            {/* Right Side - Login Form */}
            <div className="w-full lg:w-1/2 flex items-center justify-center px-6 py-12 relative">
                {/* Theme Toggle */}
                <button
                    onClick={() => setIsDark(!isDark)}
                    className="absolute top-6 left-6 w-12 h-12 rounded-full flex items-center justify-center transition-all"
                    style={{
                        backgroundColor: isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)',
                        border: `1px solid ${isDark ? 'rgba(255,255,255,0.2)' : 'rgba(0,0,0,0.1)'}`
                    }}
                >
                    {isDark ? (
                        <Sun className="w-5 h-5" style={{ color: '#ffffff' }} />
                    ) : (
                        <Moon className="w-5 h-5" style={{ color: '#0a2e2e' }} />
                    )}
                </button>

                <div className="w-full max-w-md">
                    {/* Mobile Logo */}
                    <div className="lg:hidden text-center mb-8">
                        <div className="flex items-center justify-center gap-3 mb-4">
                            <svg width="50" height="65" viewBox="0 0 60 80" fill="none">
                                <path d="M20 10 Q30 0 40 10 L40 40 Q30 50 20 40 Z" fill="#22d3ee" />
                                <path d="M20 40 Q30 30 40 40 L40 70 Q30 80 20 70 Z" fill="#f97316" />
                            </svg>
                        </div>
                        <h1 className="text-3xl font-light tracking-wider" style={{
                            color: isDark ? '#ffffff' : '#0a2e2e'
                        }}>
                            Bailey and Co.
                        </h1>
                    </div>

                    {/* Welcome Text */}
                    <div className="text-center mb-8">
                        <h2 className="text-3xl font-light mb-2" style={{
                            color: isDark ? '#ffffff' : '#0a2e2e',
                            letterSpacing: '0.05em'
                        }}>
                            Welcome
                        </h2>
                        <p className="text-sm tracking-wide" style={{
                            color: isDark ? 'rgba(255,255,255,0.6)' : 'rgba(0,0,0,0.5)',
                            textTransform: 'uppercase',
                            letterSpacing: '0.15em',
                            fontSize: '11px'
                        }}>
                            Please login to Admin Dashboard.
                        </p>
                    </div>

                    {/* Error Message */}
                    {error && (
                        <div className="mb-6 p-4 rounded-lg" style={{
                            backgroundColor: 'rgba(239, 68, 68, 0.1)',
                            border: '1px solid rgba(239, 68, 68, 0.3)'
                        }}>
                            <p className="text-sm" style={{ color: '#ef4444' }}>{error}</p>
                        </div>
                    )}

                    {/* Login Form */}
                    <div className="space-y-5">
                        {/* Username */}
                        <div>
                            <input
                                type="text"
                                placeholder="Username"
                                className="w-full px-4 py-4 rounded-lg transition-all focus:outline-none"
                                style={{
                                    backgroundColor: isDark ? 'rgba(255,255,255,0.05)' : '#ffffff',
                                    border: `1px solid ${isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)'}`,
                                    color: isDark ? '#ffffff' : '#0a2e2e',
                                    fontSize: '14px',
                                    letterSpacing: '0.02em'
                                }}
                                value={credentials.username}
                                onChange={(e) => setCredentials({ ...credentials, username: e.target.value })}
                                onFocus={(e) => {
                                    e.target.style.borderColor = isDark ? 'rgba(255,255,255,0.3)' : 'rgba(0,0,0,0.3)';
                                }}
                                onBlur={(e) => {
                                    e.target.style.borderColor = isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)';
                                }}
                            />
                        </div>

                        {/* Password */}
                        <div>
                            <input
                                type="password"
                                placeholder="Password"
                                className="w-full px-4 py-4 rounded-lg transition-all focus:outline-none"
                                style={{
                                    backgroundColor: isDark ? 'rgba(255,255,255,0.05)' : '#ffffff',
                                    border: `1px solid ${isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)'}`,
                                    color: isDark ? '#ffffff' : '#0a2e2e',
                                    fontSize: '14px',
                                    letterSpacing: '0.02em'
                                }}
                                value={credentials.password}
                                onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
                                onFocus={(e) => {
                                    e.target.style.borderColor = isDark ? 'rgba(255,255,255,0.3)' : 'rgba(0,0,0,0.3)';
                                }}
                                onBlur={(e) => {
                                    e.target.style.borderColor = isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)';
                                }}
                            />
                        </div>

                        {/* Login Button */}
                        <button
                            onClick={handleSubmit}
                            disabled={loading}
                            className="w-full py-4 rounded-lg font-medium transition-all disabled:opacity-50 disabled:cursor-not-allowed mt-8"
                            style={{
                                backgroundColor: '#f97316',
                                color: '#ffffff',
                                textTransform: 'uppercase',
                                letterSpacing: '0.1em',
                                fontSize: '13px',
                                border: 'none'
                            }}
                            onMouseEnter={(e) => {
                                if (!loading) e.target.style.backgroundColor = '#ea580c';
                            }}
                            onMouseLeave={(e) => {
                                e.target.style.backgroundColor = '#f97316';
                            }}
                        >
                            {loading ? 'Logging in...' : 'Login'}
                        </button>

                        {/* Forgot Password */}
                        <div className="text-center pt-4">
                            <button
                                className="text-sm transition-opacity hover:opacity-70"
                                style={{
                                    color: isDark ? 'rgba(255,255,255,0.6)' : 'rgba(0,0,0,0.5)',
                                    letterSpacing: '0.05em',
                                    background: 'none',
                                    border: 'none',
                                    cursor: 'pointer'
                                }}
                            >
                                Forgotten Your Password?
                            </button>
                        </div>
                    </div>

                    {/* Default Credentials */}
                    <div className="mt-8 p-4 rounded-lg text-xs" style={{
                        backgroundColor: isDark ? 'rgba(255,255,255,0.03)' : 'rgba(0,0,0,0.03)',
                        border: `1px solid ${isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)'}`,
                        color: isDark ? 'rgba(255,255,255,0.5)' : 'rgba(0,0,0,0.5)'
                    }}>
                        <strong>Default Credentials:</strong> Username: admin | Password: admin
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
