import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
    LayoutDashboard,
    Package,
    ShoppingCart,
    Users,
    Menu,
    X,
    LogOut,
    ChevronRight,
    Search,
    Bell,
    Settings,
    TrendingUp,
    DollarSign,
    Activity
} from 'lucide-react';
import {
    LineChart,
    Line,
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer
} from 'recharts';
import './Dashboard.css';

function Dashboard({ adminUser, onLogout }) {
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);
    const [pageTitle, setPageTitle] = useState('Dashboard');

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    const getPageTitle = (path) => {
        switch (path) {
            case '/dashboard': return 'Dashboard';
            case '/dashboard/categories': return 'Categories';
            case '/dashboard/subcategories': return 'Sub Categories';
            case '/dashboard/products': return 'Products';
            case '/dashboard/orders': return 'Orders';
            default: return 'Dashboard';
        }
    };

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <aside className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
                <div className="sidebar-header">
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div>
                            <h2 className="brand-title">LESTORA</h2>
                        </div>
                        <button
                            className="lg:hidden text-white"
                            onClick={toggleSidebar}
                            style={{ background: 'none', border: 'none', cursor: 'pointer', color: 'white' }}
                        >
                            <X size={24} />
                        </button>
                    </div>
                    {adminUser && (
                        <p className="user-welcome">
                            Welcome, {adminUser.username}
                        </p>
                    )}
                </div>

                <nav className="sidebar-nav">
                    <Link to="/dashboard" className="nav-link active" onClick={() => setPageTitle('Dashboard')}>
                        <LayoutDashboard className="nav-icon" />
                        Dashboard
                    </Link>
                    <Link to="/dashboard/categories" className="nav-link" onClick={() => setPageTitle('Categories')}>
                        <ChevronRight className="nav-icon" />
                        Categories
                    </Link>
                    <Link to="/dashboard/subcategories" className="nav-link" onClick={() => setPageTitle('Sub Categories')}>
                        <ChevronRight className="nav-icon" />
                        Sub Categories
                    </Link>
                    <Link to="/dashboard/products" className="nav-link" onClick={() => setPageTitle('Products')}>
                        <Package className="nav-icon" />
                        Products
                    </Link>
                    <Link to="/dashboard/orders" className="nav-link" onClick={() => setPageTitle('Orders')}>
                        <ShoppingCart className="nav-icon" />
                        Orders
                    </Link>
                </nav>

                <div className="sidebar-footer">
                    <button onClick={onLogout} className="logout-btn">
                        <LogOut size={18} style={{ marginRight: '0.5rem' }} />
                        Logout
                    </button>
                </div>
            </aside>

            {/* Main Content */}
            <main className="main-content">
                <header className="top-header">
                    <div className="header-content">
                        <button className="menu-btn" onClick={toggleSidebar}>
                            <Menu size={24} color="#002b36" />
                        </button>
                        <h1 className="header-title">{pageTitle}</h1>
                    </div>
                    <div style={{ display: 'flex', gap: '1rem' }}>
                        {/* Placeholder for header actions */}
                    </div>
                </header>

                <div className="content-wrapper">
                    <DashboardHome />
                </div>
            </main>
        </div>
    );
}

function DashboardHome() {
    const [stats, setStats] = useState({
        totalCategories: 0,
        totalProducts: 0,
        totalOrders: 0,
        pendingOrders: 0
    });
    const [loading, setLoading] = useState(true);
    const [orderData, setOrderData] = useState([]);
    const [productData, setProductData] = useState([]);

    useEffect(() => {
        fetchDashboardStats();
    }, []);

    const fetchDashboardStats = async () => {
        try {
            const { categoryAPI, productAPI, orderAPI } = await import('../services/api');

            const [categoriesRes, productsRes, ordersRes] = await Promise.all([
                categoryAPI.getAll(),
                productAPI.getAll(),
                orderAPI.getAll()
            ]);

            const categories = categoriesRes.data.categories || [];
            const products = productsRes.data.products || [];
            const orders = ordersRes.data.orders || [];

            const totalCategories = categories.length;
            const totalProducts = products.length;
            const totalOrders = orders.length;
            const pendingOrders = orders.filter(order => order.status === 'pending').length;

            setStats({
                totalCategories,
                totalProducts,
                totalOrders,
                pendingOrders
            });

            // Process Order Data for Line Chart (Last 7 Days)
            const last7Days = [...Array(7)].map((_, i) => {
                const d = new Date();
                d.setDate(d.getDate() - i);
                return d.toISOString().split('T')[0];
            }).reverse();

            const processedOrderData = last7Days.map(date => {
                const dayOrders = orders.filter(order => order.createdAt.startsWith(date));
                return {
                    date: new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
                    total: dayOrders.length,
                    pending: dayOrders.filter(o => o.status === 'pending').length
                };
            });
            setOrderData(processedOrderData);

            // Process Product Data for Bar Chart (By Category)
            const categoryCounts = {};
            products.forEach(product => {
                const catName = product.category?.name || 'Uncategorized';
                categoryCounts[catName] = (categoryCounts[catName] || 0) + 1;
            });

            const processedProductData = Object.keys(categoryCounts).map(key => ({
                name: key,
                count: categoryCounts[key]
            }));
            setProductData(processedProductData);

        } catch (error) {
            console.error('Failed to fetch dashboard stats:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="spinner"></div>
            </div>
        );
    }

    return (
        <div className="dashboard-home">
            {/* Stats Grid */}
            <div className="stats-grid">
                <StatCard
                    title="Total Categories"
                    value={stats.totalCategories}
                    icon={<LayoutDashboard />}
                    colorClass="stat-card-purple"
                />
                <StatCard
                    title="Total Products"
                    value={stats.totalProducts}
                    icon={<Package />}
                    colorClass="stat-card-pink"
                />
                <StatCard
                    title="Total Orders"
                    value={stats.totalOrders}
                    icon={<ShoppingCart />}
                    colorClass="stat-card-green"
                />
                <StatCard
                    title="Pending Orders"
                    value={stats.pendingOrders}
                    icon={<Activity />}
                    colorClass="stat-card-amber"
                />
            </div>

            {/* Charts Section */}
            <div className="charts-container">
                <div className="chart-wrapper">
                    <h3 className="chart-title">Order Trends (Last 7 Days)</h3>
                    <div style={{ width: '100%', height: 300 }}>
                        <ResponsiveContainer>
                            <LineChart data={orderData}>
                                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                                <XAxis dataKey="date" stroke="#94a3b8" />
                                <YAxis stroke="#94a3b8" />
                                <Tooltip
                                    contentStyle={{ backgroundColor: '#002b36', border: '1px solid rgba(34, 211, 238, 0.3)', borderRadius: '8px', color: '#fff' }}
                                    itemStyle={{ color: '#fff' }}
                                />
                                <Legend />
                                <Line type="monotone" dataKey="total" name="Total Orders" stroke="#22d3ee" strokeWidth={2} activeDot={{ r: 8 }} />
                                <Line type="monotone" dataKey="pending" name="Pending Orders" stroke="#f59e0b" strokeWidth={2} />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>
                </div>

                <div className="chart-wrapper">
                    <h3 className="chart-title">Products by Category</h3>
                    <div style={{ width: '100%', height: 300 }}>
                        <ResponsiveContainer>
                            <BarChart data={productData}>
                                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                                <XAxis dataKey="name" stroke="#94a3b8" />
                                <YAxis stroke="#94a3b8" />
                                <Tooltip
                                    contentStyle={{ backgroundColor: '#002b36', border: '1px solid rgba(34, 211, 238, 0.3)', borderRadius: '8px', color: '#fff' }}
                                    cursor={{ fill: 'rgba(255,255,255,0.05)' }}
                                />
                                <Legend />
                                <Bar dataKey="count" name="Products" fill="#f093fb" radius={[4, 4, 0, 0]} />
                            </BarChart>
                        </ResponsiveContainer>
                    </div>
                </div>
            </div>

            {/* Welcome Card */}
            <div className="welcome-card">
                <h2>Welcome to LESTORA Dashboard</h2>
                <p>
                    Manage your e-commerce platform with ease. Track your sales, manage inventory,
                    and process orders all in one place.
                </p>
                <div className="features-grid">
                    <div className="feature-box">
                        <strong>Quick Actions</strong>
                        <span>Create new products or categories instantly</span>
                    </div>
                    <div className="feature-box">
                        <strong>Real-time Data</strong>
                        <span>Live updates on orders and inventory</span>
                    </div>
                    <div className="feature-box">
                        <strong>Analytics</strong>
                        <span>Track performance and growth</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

function StatCard({ title, value, icon, colorClass }) {
    return (
        <div className={`stat-card ${colorClass}`}>
            <div className="stat-icon-wrapper">
                {icon}
            </div>
            <div className="stat-info">
                <h3>{title}</h3>
                <p>{value}</p>
            </div>
        </div>
    );
}

export default Dashboard;
